Param(
	[parameter(Mandatory=$False)] 
	[string]$ServerName="LocalHost",
	
	[parameter(Mandatory=$False)] 
	[string]$User=$env:username,
	
	[parameter(Mandatory=$False)] 
	[string]$RASSite="All",
	
	[parameter(Mandatory=$False)] 
