#region process farm
Function ProcessFarm
{
	Write-Verbose "$(Get-Date -Format G): Processing Farm"
	
	$Results = Get-RASFarmSettings -EA 0 4>$Null

	If(!$?)
	{
		Write-Error "
		`n`n
		Unable to retrieve RAS Farm Settings for Parallels RAS server $Script:ServerName.`
		`n`n
		Script cannot continue.
		`n`n
		"
		AbortScript
	}
	ElseIf($? -and $Null -eq $Results)
	{
		Write-Error "
		`n`n
		No RAS Farm Settings retrieved for Parallels RAS server $Script:ServerName.`
		`n`n
		Script cannot continue.
		`n`n
		"
		AbortScript
	}
	Else
	{
		$Script:RASFarmName = $Results.Name
	}
}

Function OutputFarm
{
	Write-Verbose "$(Get-Date -Format G): Output Farm"
	
	If($MSWord -or $PDF)
	{
		$Script:Selection.InsertNewPage()
		WriteWordLine 1 0 "Farm - " $Script:RASFarmName
	}
	If($Text)
	{
		Line 0 "Farm - " $Script:RASFarmName
	}
	If($HTML)
	{
		WriteHTMLLine 1 0 "Farm - " $Script:RASFarmName
	}
}

Function GetFarmSites
{
	#Write-Verbose "$(Get-Date -Format G): `tRetrieving Farm Sites"
	
	If($RASSite -eq "All")
	{
		$Script:Sites = Get-RASSite -EA 0 4> $Null
	}
	Else
	{
		$Script:Sites = Get-RASSite -Name $RASSite -EA 0 4> $Null
	}

	If(!$?)
	{
		If($RASSite -eq "All")
		{
	Write-Error "
	`n`n
	Unable to retrieve Sites for Farm $Script:RASFarmName
	`n`n
	Script cannot continue
	`n`n
	"
		}
		Else
		{
	Write-Error "
	`n`n
	Unable to retrieve Site $RASSite for Farm $Script:RASFarmName
	`n`n
	Script cannot continue
	`n`n
	"
		}
		AbortScript
	}
	ElseIf($? -and $Null -eq $Script:Sites)
	{
	Write-Warning "
	`n
	No Sites were found for Farm $Script:RASFarmName.`
	`n
	"
		If($MSWord -or $PDF)
		{
			WriteWordLine 0 0 "No Sites were found for Farm $Script:RASFarmName"
		}
		If($Text)
		{
			Line 0 "No Sites were found for Farm $Script:RASFarmName"
		}
		If($HTML)
		{
			WriteHTMLLine 0 0 "No Sites were found for Farm $Script:RASFarmName"
		}
	}
	Else
	{
		#Continue on with script
	}
}

Function OutputFarmSite
{
	Param([object]$Site)
	
	Write-Verbose "$(Get-Date -Format G): `tOutput Farm Site $($Site.Name)"
	$SiteSettings = Get-RASSiteStatus -Siteid $Site.Id -EA 0 4> $Null
	
	If(!$?)
	{
		Write-Warning "
		`n
		Unable to retrieve Site Status for Site $Site.Name`
		"
		$PrimaryPublishingAgent = "N/A"
		$Type                   = "N/A"
		$State                  = "N/A"
		$Description            = "Can't find"
		$ID                     = $Site.Id
		If($MSWord -or $PDF)
		{
			WriteWordLine 0 0 "Unable to retrieve Site Status for Site $Site.Name"
		}
		If($Text)
		{
			Line 0 "Unable to retrieve Site Status for Site $Site.Name"
		}
		If($HTML)
		{
			WriteHTMLLine 0 0 "Unable to retrieve Site Status for Site $Site.Name"
		}
	}
	ElseIf($? -and $Null -eq $SiteSettings)
	{
		Write-Host "
	No Site Status retrieved for Site $Site.Name.`
		" -ForegroundColor White
		$PrimaryPublishingAgent = "N/A"
		If($Site.LicensingSite)
		{
			$Type = "Licensing Site/Current Site"
		}
		Else
		{
			$Type = "Secondary Site"
		}
		$State = "N/A"
		$Description = "Can't find"
		$ID = $Site.Id
		If($MSWord -or $PDF)
		{
			WriteWordLine 0 0 "No Site Status retrieved for Site $Site.Name"
		}
		If($Text)
		{
			Line 0 "No Site Status retrieved for Site $Site.Name"
		}
		If($HTML)
		{
			WriteHTMLLine 0 0 "No Site Status retrieved for Site $Site.Name"
		}
	}
	Else
	{
		$Results = Get-RASBroker -SiteId $Site.Id -EA 0 4> $Null | Select-Object -First 1 #changed in 2.52 to only get the first PA
		
		If($? -and $Null -ne $Results)
		{
			$Description = $Results.Description
		}
		Else
		{
			$Description = "-"
		}
		
		$PrimaryPublishingAgent = $SiteSettings.Server
		If($Site.LicensingSite)
		{
			$Type = "Licensing Site/Local Site/Current Site"
		}
		Else
		{
			$Type = "Secondary Site"
		}
		$State = GetRASStatus $SiteSettings.AgentState
		$ID = $Site.Id
	}
	
	If($MSWord -or $PDF)
	{
		$ScriptInformation = New-Object System.Collections.ArrayList
		$ScriptInformation.Add(@{Data = "Site"; Value = $Site.Name; }) > $Null
		$ScriptInformation.Add(@{Data = "Primary Connection Broker"; Value = $PrimaryPublishingAgent; }) > $Null
		$ScriptInformation.Add(@{Data = "Type"; Value = $Type; }) > $Null
		$ScriptInformation.Add(@{Data = "State"; Value = $State; }) > $Null
		$ScriptInformation.Add(@{Data = "Description"; Value = $Description; }) > $Null
		$ScriptInformation.Add(@{Data = "Last modification by"; Value = $Site.AdminLastMod; }) > $Null
		$ScriptInformation.Add(@{Data = "Modified on"; Value = (Get-Date -UFormat "%c" $Site.TimeLastMod); }) > $Null
		$ScriptInformation.Add(@{Data = "Created by"; Value = $Site.AdminCreate; }) > $Null
		$ScriptInformation.Add(@{Data = "Created on"; Value = (Get-Date -UFormat "%c" $Site.TimeCreate); }) > $Null
		$ScriptInformation.Add(@{Data = "ID"; Value = $ID; }) > $Null

		$Table = AddWordTable -Hashtable $ScriptInformation `
		-Columns Data,Value `
		-List `
		-Format $wdTableGrid `
		-AutoFit $wdAutoFitFixed;

		SetWordCellFormat -Collection $Table -Size 10 -BackgroundColor $wdColorWhite
		SetWordCellFormat -Collection $Table.Columns.Item(1).Cells -Bold -BackgroundColor $wdColorGray15;

		$Table.Columns.Item(1).Width = 200;
		$Table.Columns.Item(2).Width = 250;

		$Table.Rows.SetLeftIndent($Indent0TabStops,$wdAdjustProportional)

		FindWordDocumentEnd
		$Table = $Null
		WriteWordLine 0 0 ""
	}
	If($Text)
	{
		Line 1 "Site`t`t`t : " $Site.Name
		Line 1 "Primary Connection Broker: " $PrimaryPublishingAgent
		Line 1 "Type`t`t`t : " $Type
		Line 1 "State`t`t`t : " $State
		Line 1 "Description`t`t : " $Description
		Line 1 "Last modification by`t : " $Site.AdminLastMod
		Line 1 "Modified on`t`t : " (Get-Date -UFormat "%c" $Site.TimeLastMod)
		Line 1 "Created by`t`t : " $Site.AdminCreate
		Line 1 "Created on`t`t : " (Get-Date -UFormat "%c" $Site.TimeCreate)
		Line 1 "ID`t`t`t : " $ID
		Line 0 ""
	}
	If($HTML)
	{
		$rowdata = @()
		$columnHeaders = @("Site",($Script:htmlsb),$Site.Name,$htmlwhite)
		$rowdata += @(,("Primary Connection Broker",($Script:htmlsb),$PrimaryPublishingAgent,$htmlwhite))
		$rowdata += @(,("Type",($Script:htmlsb),$Type,$htmlwhite))
		$rowdata += @(,("State",($Script:htmlsb),$State.ToString(),$htmlwhite))
		$rowdata += @(,("Description",($Script:htmlsb),$Description,$htmlwhite))
		$rowdata += @(,("Last modification by",($Script:htmlsb), $Site.AdminLastMod,$htmlwhite))
		$rowdata += @(,("Modified on",($Script:htmlsb), (Get-Date -UFormat "%c" $Site.TimeLastMod),$htmlwhite))
		$rowdata += @(,("Created by",($Script:htmlsb), $Site.AdminCreate,$htmlwhite))
		$rowdata += @(,("Created on",($Script:htmlsb), (Get-Date -UFormat "%c" $Site.TimeCreate),$htmlwhite))
		$rowdata += @(,("ID",($Script:htmlsb),$ID,$htmlwhite))

		$msg = ""
		$columnWidths = @("200","275")
		FormatHTMLTable $msg "auto" -rowArray $rowdata -columnArray $columnHeaders -fixedWidth $columnWidths
		WriteHTMLLine 0 0 ""
	}

	#added Properties in V4
	If($MSWord -or $PDF)
	{
		WriteWordLine 2 0 "$($Site.Name) Properties"
		$ScriptInformation = New-Object System.Collections.ArrayList
		$ScriptInformation.Add(@{Data = "Site"; Value = $Site.Name; }) > $Null
		$ScriptInformation.Add(@{Data = "Server"; Value = $PrimaryPublishingAgent; }) > $Null
		$ScriptInformation.Add(@{Data = "Description"; Value = $Description; }) > $Null

		$Table = AddWordTable -Hashtable $ScriptInformation `
		-Columns Data,Value `
		-List `
		-Format $wdTableGrid `
		-AutoFit $wdAutoFitFixed;

		SetWordCellFormat -Collection $Table -Size 10 -BackgroundColor $wdColorWhite
		SetWordCellFormat -Collection $Table.Columns.Item(1).Cells -Bold -BackgroundColor $wdColorGray15;

		$Table.Columns.Item(1).Width = 200;
		$Table.Columns.Item(2).Width = 250;

		$Table.Rows.SetLeftIndent($Indent0TabStops,$wdAdjustProportional)

		FindWordDocumentEnd
		$Table = $Null
		WriteWordLine 0 0 ""

		#Access addresses in not availble in PowerShell 19.x
		If($Script:RASMajorVersion -ge 20)
		{
			$AccessAddresses = Get-RASSiteAccessAddresses -Id $Site.Id -EA 0 4>$Null
			
			<#
				$AccessAddresses |fl

				Name          : TBGateway
				SiteId        : 0
				Description   :
				PublicAddress : workspaceus.parallelsras.net
				IPs           :
				Type          : Custom
				Port          : 0
				SSLPort       : 443
			#>
			
			WriteWordLine 3 0 "Access addresses"
			$ScriptInformation = New-Object System.Collections.ArrayList
			If($? -and $Null -ne $AccessAddresses)
			{
				ForEach($AccessAddress in $AccessAddresses)
				{
					$ScriptInformation.Add(@{Data = "Name"; Value = $AccessAddress.Name; }) > $Null
					$ScriptInformation.Add(@{Data = "Description"; Value = $AccessAddress.Description; }) > $Null
					$ScriptInformation.Add(@{Data = "Public Address"; Value = $AccessAddress.PublicAddress; }) > $Null
					$ScriptInformation.Add(@{Data = "IPs"; Value = $AccessAddress.IPs; }) > $Null
					$ScriptInformation.Add(@{Data = "Type"; Value = $AccessAddress.Type; }) > $Null
					$ScriptInformation.Add(@{Data = "Port"; Value = $AccessAddress.Port.ToString(); }) > $Null
					$ScriptInformation.Add(@{Data = "SSL Port"; Value = $AccessAddress.SSLPort.ToString(); }) > $Null
					$ScriptInformation.Add(@{Data = ""; Value = ""; }) > $Null
				}
			}
			Else
			{
				$ScriptInformation.Add(@{Data = "Name"; Value = ""; }) > $Null
				$ScriptInformation.Add(@{Data = "Description"; Value = ""; }) > $Null
				$ScriptInformation.Add(@{Data = "Public Address"; Value = ""; }) > $Null
				$ScriptInformation.Add(@{Data = "IPs"; Value = ""; }) > $Null
				$ScriptInformation.Add(@{Data = "Type"; Value = ""; }) > $Null
				$ScriptInformation.Add(@{Data = "Port"; Value = ""; }) > $Null
				$ScriptInformation.Add(@{Data = "SSL Port"; Value = ""; }) > $Null
				$ScriptInformation.Add(@{Data = ""; Value = ""; }) > $Null
			}

			$Table = AddWordTable -Hashtable $ScriptInformation `
			-Columns Data,Value `
			-List `
			-Format $wdTableGrid `
			-AutoFit $wdAutoFitFixed;

			SetWordCellFormat -Collection $Table -Size 10 -BackgroundColor $wdColorWhite
			SetWordCellFormat -Collection $Table.Columns.Item(1).Cells -Bold -BackgroundColor $wdColorGray15;

			$Table.Columns.Item(1).Width = 200;
			$Table.Columns.Item(2).Width = 250;

			$Table.Rows.SetLeftIndent($Indent0TabStops,$wdAdjustProportional)

			FindWordDocumentEnd
			$Table = $Null
			WriteWordLine 0 0 ""
		}
		
		$ScriptInformation = New-Object System.Collections.ArrayList
		$ScriptInformation.Add(@{Data = "Enable HTTP host header attacks protection"; Value = $Site.EnableHttpHostAttackProtection.ToString(); }) > $Null

		$Table = AddWordTable -Hashtable $ScriptInformation `
		-Columns Data,Value `
		-List `
		-Format $wdTableGrid `
		-AutoFit $wdAutoFitFixed;

		SetWordCellFormat -Collection $Table -Size 10 -BackgroundColor $wdColorWhite
		SetWordCellFormat -Collection $Table.Columns.Item(1).Cells -Bold -BackgroundColor $wdColorGray15;

		$Table.Columns.Item(1).Width = 200;
		$Table.Columns.Item(2).Width = 250;

		$Table.Rows.SetLeftIndent($Indent0TabStops,$wdAdjustProportional)

		FindWordDocumentEnd
		$Table = $Null
		WriteWordLine 0 0 ""
	}
	If($Text)
	{
		Line 1 "$($Site.Name) Properties"
		Line 2 "Site`t`t : " $Site.Name
		Line 2 "Server`t`t : " $PrimaryPublishingAgent
		Line 2 "Description`t : " $Description
		Line 0 ""

		#Access addresses in not availble in PowerShell 19.x
		If($Script:RASMajorVersion -ge 20)
		{
			$AccessAddresses = Get-RASSiteAccessAddresses -Id $Site.Id -EA 0 4>$Null
			
			<#
				$AccessAddresses |fl

				Name          : TBGateway
				SiteId        : 0
				Description   :
				PublicAddress : workspaceus.parallelsras.net
				IPs           :
				Type          : Custom
				Port          : 0
				SSLPort       : 443
			#>
			
			Line 2 "Access addresses"
			If($? -and $Null -ne $AccessAddresses)
			{
				ForEach($AccessAddress in $AccessAddresses)
				{
					Line 3 "Name`t`t: " $AccessAddress.Name
					Line 3 "Description`t: " $AccessAddress.Description
					Line 3 "Public Address`t: " $AccessAddress.PublicAddress
					Line 3 "IPs`t`t: " $AccessAddress.IPs
					Line 3 "Type`t`t: " $AccessAddress.Type
					Line 3 "Port`t`t: " $AccessAddress.Port.ToString()
					Line 3 "SSL Port`t: " $AccessAddress.SSLPort.ToString()
					Line 0 ""
				}
			}
			Else
			{
				Line 3 "Name`t`t: "
				Line 3 "Description`t: "
				Line 3 "Public Address`t: "
				Line 3 "IPs`t`t: "
				Line 3 "Type`t`t: "
				Line 3 "Port`t`t: "
				Line 3 "SSL Port`t: "
				Line 0 ""
			}
		}
		
		Line 2 "Enable HTTP host header attacks protection: " $Site.EnableHttpHostAttackProtection.ToString()
		Line 0 ""
	}
	If($HTML)
	{
		WriteHTMLLine 2 0 "$($Site.Name) Properties"

		$rowdata = @()
		$columnHeaders = @("Site",($Script:htmlsb),$Site.Name,$htmlwhite)
		$rowdata += @(,("Server",($Script:htmlsb),$PrimaryPublishingAgent,$htmlwhite))
		$rowdata += @(,("Description",($Script:htmlsb),$Description,$htmlwhite))

		$msg = ""
		$columnWidths = @("200","275")
		FormatHTMLTable $msg "auto" -rowArray $rowdata -columnArray $columnHeaders -fixedWidth $columnWidths
		WriteHTMLLine 0 0 ""

		#Access addresses in not availble in PowerShell 19.x
		If($Script:RASMajorVersion -ge 20)
		{
			$AccessAddresses = Get-RASSiteAccessAddresses -Id $Site.Id -EA 0 4>$Null
			
			<#
				$AccessAddresses |fl

				Name          : TBGateway
				SiteId        : 0
				Description   :
				PublicAddress : workspaceus.parallelsras.net
				IPs           :
				Type          : Custom
				Port          : 0
				SSLPort       : 443
			#>
			
			WriteHTMLLine 3 0 "Access addresses"
			$rowdata = @()
			If($? -and $Null -ne $AccessAddresses)
			{
				$xCnt = -1
				ForEach($AccessAddress in $AccessAddresses)
				{
					$xCnt++
					
					If($xCnt -eq 0)
					{
						$columnHeaders = @("Name",($Script:htmlsb),$AccessAddress.Name,$htmlwhite)
					}
					Else
					{
						$rowdata += @(,("Name",($Script:htmlsb),$AccessAddress.Name,$htmlwhite))
					}
					$rowdata += @(,("Description",($Script:htmlsb),$AccessAddress.Description,$htmlwhite))
					$rowdata += @(,("Public Address",($Script:htmlsb),$AccessAddress.PublicAddress,$htmlwhite))
					$rowdata += @(,("IPs",($Script:htmlsb),$AccessAddress.IPs,$htmlwhite))
					$rowdata += @(,("Type",($Script:htmlsb),$AccessAddress.Type,$htmlwhite))
					$rowdata += @(,("Port",($Script:htmlsb),$AccessAddress.Port.ToString(),$htmlwhite))
					$rowdata += @(,("SSL Port",($Script:htmlsb),$AccessAddress.SSLPort.ToString(),$htmlwhite))
					$rowdata += @(,("",($Script:htmlsb),"",$htmlwhite))
				}
			}
			Else
			{
				$columnHeaders = @("Name",($Script:htmlsb),"",$htmlwhite)
				$rowdata += @(,("Description",($Script:htmlsb),"",$htmlwhite))
				$rowdata += @(,("Public Address",($Script:htmlsb),"",$htmlwhite))
				$rowdata += @(,("IPs",($Script:htmlsb),"",$htmlwhite))
				$rowdata += @(,("Type",($Script:htmlsb),"",$htmlwhite))
				$rowdata += @(,("Port",($Script:htmlsb),"",$htmlwhite))
				$rowdata += @(,("SSL Port",($Script:htmlsb),"",$htmlwhite))
			}

			$msg = ""
			$columnWidths = @("200","275")
			FormatHTMLTable $msg "auto" -rowArray $rowdata -columnArray $columnHeaders -fixedWidth $columnWidths
			WriteHTMLLine 0 0 ""
		}

		$rowdata = @()
		$columnHeaders = @("Enable HTTP host header attacks protection",($Script:htmlsb),$Site.EnableHttpHostAttackProtection.ToString(),$htmlwhite)

		$msg = ""
		$columnWidths = @("250","225")
		FormatHTMLTable $msg "auto" -rowArray $rowdata -columnArray $columnHeaders -fixedWidth $columnWidths
		WriteHTMLLIne 0 0 ""
	}
}

