					$RDSRestrictedUsers       = @()
					
					ForEach($User in $RDSHost.RestrictedUsers)
					{
						$objSID  = New-Object System.Security.Principal.SecurityIdentifier ($User)
						$objUser = $objSID.Translate( [System.Security.Principal.NTAccount])
						
						$RDSRestrictedUsers += $objUser.Value
					}
