						"OnlyROProfile"	{$FSLogixAS_ProfileType = "Only RO profile"; Break}
						"RWROProfile"	{$FSLogixAS_ProfileType = "RW/RO profile"; Break}
						Default			{$FSLogixAS_ProfileType = "Unknown: $($FSLogixAS.ProfileType)"; Break}
