					$FSLogixAS_ReAttachIntervalSeconds = $FSLogixAS.ReAttachIntervalSeconds                
					$FSLogixAS_ReAttachRetryCount      = $FSLogixAS.ReAttachRetryCount                     

					Switch($FSLogixAS.RemoveOrphanedOSTFilesOnLogoff)
