					}
						
					$VDIPoolMembers = Get-RASVDIHostPoolMember -SiteId $Site.Id -VDIHostPoolName $VDIPool.Name -EA 0 4>$Null 
					
					If($? -and $Null -ne $VDIPoolMembers)
