			If($Text)
			{
				Line 3 "Inherit default settings`t`t: " $SecureGateway.InheritDefaultSecuritySettings.ToString()
				If($SecureGatewayAllow -eq "AllowAllExcept")
				{
					Line 3 "Allow all except"
				}
				Else
				{
					Line 3 "Allow only"
				}
				$cnt =-1
				ForEach($Item in $MACAddresses)
