			}
			If($HTML)
			{
				#Nothing
			}
			
			Switch($HALB.IPVersion)
			{
				"Version4"		{$HALBIPVersion = "Version 4"; Break}
				"Version6"		{$HALBIPVersion = "Version 6"; Break}
