				$columnHeaders = @("Name",($Script:htmlsb),$Cert.Name.Replace("<","").Replace(">",""),$htmlwhite)
				$rowdata += @(,("Description",($Script:htmlsb),$Cert.Description,$htmlwhite))
				$rowdata += @(,("Usage",($Script:htmlsb),$Cert.Usage,$htmlwhite))
